string input = "";

char[] chars = new char[] { '/', '/', '/', '/' };

for (int i = 0; i < input.Length; i++)
{
    chars[0] = chars[1];
    chars[1] = chars[2];
    chars[2] = chars[3];
    chars[3] = input[i];
    if (chars.Distinct().Count() is 4 && !chars.Contains('/'))
    {
        Console.WriteLine(i + 1);
        break;
    }
}
